            setParameter(Vocabulary.GET_OWNED_VOC_LIST_ROLES, roles);
    q = TemporalUtils.setDatetimeConstantParameters(q);
    List<Vocabulary> entityList = q.getResultList();
    em.close();
    return entityList;
}

